import React, { useEffect, useState } from 'react'
import './CSS/Product.css'
import {publicRequest} from '../requestMethods'
import Products from './Products'
import { useDispatch, useSelector } from 'react-redux'
import { addFilterArray, addGender, addproduct, clearAll, clearFilterArray, fillAll, sortAscFilterArray, sortDescFilterArray } from '../Redux/productRedux'
// import { products } from '../Data'

const Product = () => {

  // const [products,setProducts] = useState([])
  
  const dispatch = useDispatch();
  const productsArray = useSelector(state => state.productFilter.products)
  const filtersArray = useSelector(state => state.productFilter.filterArray);
  const genderArray = useSelector(state => state.productFilter.genderArray);
  const filters = useSelector(state => state.productFilter.filters)
  // const products = filtersArray.length===0 ? productsArray : filtersArray

  useEffect(()=>{

    dispatch(clearAll({}))
    dispatch(fillAll({}))
    // console.log(filtersArray,"filter")

    document.querySelectorAll('.product-dropdown').forEach(item => {
      const select = item.querySelector('.product-dropdown-select')
      const arrow = item.querySelector('.product-dropdown-pointer')
      const menu = item.querySelector('.product-dropdown-menu')
      const options = item.querySelectorAll('.product-dropdown-menu li')

      select.addEventListener('click', () => {
        menu.classList.toggle('product-dropdown-menu-open')
        arrow.classList.toggle('product-dropdown-pointer-rotate')
      })

      options.forEach(option => {
        option.addEventListener('click', () => {
          menu.classList.toggle('product-dropdown-menu-open')
          arrow.classList.toggle('product-dropdown-pointer-rotate')
          document.querySelector('.product-dropdown-selected').innerHTML = option.innerHTML
        })
      })
        
 
    })

  },[])

  function sort(e){
    dispatch(clearFilterArray({}))
    let gender = 'everyone'
    dispatch(addGender({gender}))
    productsArray.map(item => {dispatch(addFilterArray({...item}))})
    if(e.target.value==='LtoH'){
      dispatch(sortAscFilterArray({}))
    }
    else if(e.target.value==='HtoL'){
      dispatch(sortDescFilterArray({}))
    }
  }



  return (
    <div className='product-container'>
        <div className='product-header'>
            {/* <select className='product-select' onChange={(e)=>sort(e)}>
                <option>sort by:Recommended</option>
                <option value='HtoL'>Price:High-Low</option>
                <option value='LtoH'>Price:Low-High</option>
            </select> */}
            <div className='product-dropdown'>
              <div className='product-dropdown-select'>
                <span className='product-dropdown-selected'>something</span>
                <div className='product-dropdown-pointer'></div>
              </div>
              <ul className='product-dropdown-menu'>
                <li className='product-dropdown-menu-active'>Recommended</li>
                <li>Low - High</li>
                <li>High - Low</li>
              </ul>
            </div>
        </div>
        <div className='product-info'>
          {
          
          filtersArray.filter(item => {
            if(filters.length===0){
              return item;
            }
            else{
              for(let i=0;i<filters.length;i++){
                if(filters[i].brand === item.brand){
                  return item;
                }
              }
            }
           
          }).map((item) => (
              <Products item={item} key={item.id}/>
            ))
          }
        </div>
    </div>
  )
}

export default Product
