import React from 'react'
import './CSS/Products.css'
import { img } from '../Data'

const Products = ({item}) => {

  // document.querySelector('.products-info').addEventListener('mouseover',()=>{
  //   console.log("ys")
  //   document.querySelector('.products-btn-wishlist').st
  // })

  function wishlistOnClick(){
    console.log("hello")
  }

  function cartOnClick(){
    console.log("hello")
  }

  return (
    <div className='products-container'>
       <img src={item.img} alt='product'/>
       <div className='products-info products-hover'>
        <div className='products-btn-wrapper'>
            <button id='products-btn products-wishlist-btn' onClick={wishlistOnClick}>
              <span class="web_sprite wishlist-icon"></span>
               WISHLIST
              </button>
            <button id='products-btn products-cart-btn' onClick={cartOnClick}>
               <span className='web_sprite cart-icon'></span>
              ADD TO CART
              </button>
        </div>
        <div className='products-desc'>
           <div className='products-desc-brand'>{item.brand}</div>
           <div className='products-desc-title'>{item.title}</div>
           <div className='products-desc-price'>&#8377;{item.price}</div>
        </div>
       </div>
    </div>
  )
}

export default Products
