import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  addFilter,
  addGender,
  addproduct,
  clearAll,
  fillFilter,
  removeFilter,
  removeGender,
} from "../Redux/productRedux";
import "./CSS/Sidepanel.css";

const Sidepanel = () => {
  const mystyle = {
    //styling
    fontWeight: 600,
  };

  const dispatch = useDispatch();
  const productArray = useSelector((state) => state.productFilter.products);
  const filtersArray = useSelector((state) => state.productFilter.filters);

  useEffect(() => {
    const checkbox = document.querySelectorAll(".filter-checkbox");

    for (let check of checkbox) {
      check.addEventListener("click", function (e) {
        const brand = e.target.value;
        if (e.target.checked === true) {
          dispatch(addFilter({ brand }));
        } else {
          dispatch(removeFilter({ brand }));
        }
      });
    }

    const radio = document.querySelectorAll(".filter-radiobtn");
    for (let btn of radio) {
      btn.addEventListener("click", function (e) {
        if (e.target.checked) {
          dispatch(clearAll({}));

          const gender = e.target.value;
          dispatch(addGender({ gender }));

          filtersArray
            .filter((item) => item.category === e.target.value)
            .map((item) => {
              dispatch(addFilter({ ...item }));
            });
        }
      });
    }
  });

  function brandFilter(){

  }

  let brandFilters = filtersArray.map((item) => {
    return (
      <div>
        <input
          type="checkbox"
          className="filter-checkbox"
          id={item.brand}
          value={item.brand}
        ></input>
        <label for={item.brand}>{item.brand}</label>
      </div>
    );
  });

  function clear() {
    dispatch(clearAll({}));
    dispatch(fillFilter({}));
    console.log(filtersArray);
  }

  return (
    <div className="home-content">
      <div className="home-filter">
        <div className="filter-heading">FILTERS</div>
        <div
          className="filter-gender"
          id="filter-container-gender"
          style={mystyle}
        >
          {/*styling*/}
          <button onClick={() => clear()}>click</button>
          <label for="Men" id="gender-label">
            <input
              type="Radio"
              className="filter-radiobtn"
              id="men-checkbox"
              name="radio-btn"
              value="men"
            ></input>
            Men
          </label>
          <label for="Women" id="gender-label">
            <input
              type="Radio"
              className="filter-radiobtn"
              id="women-checkbox"
              name="radio-btn"
              value="women"
            ></input>
            Women
          </label>
          <label for="Boys" id="gender-label">
            <input
              type="Radio"
              className="filter-radiobtn"
              name="radio-btn"
              value="boys"
            ></input>
            Boys
          </label>
          <label for="Girls" id="gender-label">
            <input
              type="Radio"
              className="filter-radiobtn"
              name="radio-btn"
              value="girls"
            ></input>
            Girls
          </label>
        </div>
        <div className="filter-brand" id="filter-container-brand">
          <div className="filter-brand-heading">BRAND</div>
          {brandFilters}
        </div>
      </div>
    </div>
  );
};

export default Sidepanel;
