import { createSlice } from "@reduxjs/toolkit";

const productSlice = createSlice({
    name : "productFilter",
    initialState : {
        products : [],
        gender : {gender : 'everyone'},
        filters : [],
    },
    reducers : {
        addproduct : (state,action) => {
            if(!state.products.find(e => e._id === action.payload._id)){
                state.products.push(action.payload)
            }
        },
        removeproduct : (state,action) => {
            state.products = state.products.filter(e => e._id !== action.payload._id)
        },
        addGender : (state,action) =>{
            state.gender = action.payload
        },
        removeGender : (state,action) =>{
            state.gender = '';
        },
        addFilter : (state,action) => {
            state.filters.push(action.payload)
        },
        removeFilter : (state,action) => {
            state.filters = state.filters.filter(e => e.brand !== action.payload.brand)
        },
        fillFilter : (state,action) => {
            state.products.map((item)=>{
                state.filter.push(item)
            })
        },
        clearAll : (state,action) =>{
            state.filters = []
            state.gender.gender = 'everyone';
        }
    }
})

export const {addFilter,removeFilter,addGender,removeGender,addproduct,removeproduct,clearAll} = productSlice.actions;
export default productSlice.reducer;
