import React, { useEffect, useState } from 'react'
import './CSS/Product.css'
import {publicRequest} from '../requestMethods'
import Products from './Products'
import { useDispatch, useSelector } from 'react-redux'
import { addFilterArray, addGender, addproduct, clearAll, clearFilterArray, fillAll, sortAscFilterArray, sortDescFilterArray } from '../Redux/productRedux'
// import { products } from '../Data'

const Product = () => {

  // const [products,setProducts] = useState([])
  
  const dispatch = useDispatch();
  const productsArray = useSelector(state => state.productFilter.products)
  const filtersArray = useSelector(state => state.productFilter.filterArray);
  const genderArray = useSelector(state => state.productFilter.genderArray);
  const filters = useSelector(state => state.productFilter.filters)
  // const products = filtersArray.length===0 ? productsArray : filtersArray

  useEffect(()=>{

    dispatch(clearAll({}))
    dispatch(fillAll({}))
    // console.log(filtersArray,"filter")

  },[])

  function sort(e){
    dispatch(clearFilterArray({}))
    let gender = 'everyone'
    dispatch(addGender({gender}))
    productsArray.map(item => {dispatch(addFilterArray({...item}))})
    if(e.target.value==='LtoH'){
      dispatch(sortAscFilterArray({}))
    }
    else if(e.target.value==='HtoL'){
      dispatch(sortDescFilterArray({}))
    }
  }



  return (
    <div>
        <div className='product-container'>
            <div className='product-header'>
                <select className='product-select' onChange={(e)=>sort(e)}>
                    <option>sort by:Recommended</option>
                    <option value='HtoL'>Price:High-Low</option>
                    <option value='LtoH'>Price:Low-High</option>
                </select>
            </div>
            <div className='product-info'>
              {
              
              filtersArray.filter(item => {
                if(filters.length===0){
                  return item;
                }
                else{
                  for(let i=0;i<filters.length;i++){
                    if(filters[i].brand === item.brand){
                      return item;
                    }
                  }
                }
               
              }).map((item) => (
                  <Products item={item} key={item.id}/>
                ))
              }
            </div>
        </div>
    </div>
  )
}

export default Product
