import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  addFilter,
  addGender,
  addGenderArray,
  addproduct,
  clearAll,
  fillFilter,
  removeFilter,
  removeGender,
} from "../Redux/productRedux";
import "./CSS/Sidepanel.css";

const Sidepanel = () => {
  const mystyle = {
    //styling
    fontWeight: 600,
  };

  const dispatch = useDispatch();
  const productArray = useSelector((state) => state.productFilter.products);
  const genderArray = useSelector(state => state.productFilter.genderArray);
  const filtersArray = useSelector((state) => state.productFilter.filters);
  const list = filtersArray
  const filter = []



  useEffect(() => {

    // const checkbox = document.querySelectorAll(".filter-checkbox");

    // for (let check of checkbox) {
    //   check.addEventListener("click", function (e) {
    //     const brand = e.target.value;
    //     if (e.target.checked === true) {
    //       dispatch(addFilter({ brand }));
    //     } else {
    //       dispatch(removeFilter({ brand }));
    //     }
    //   });
    // }

    const radio = document.querySelectorAll(".filter-radiobtn");
    for (let btn of radio) {
      btn.addEventListener("click", function (e) {
        if (e.target.checked) {
          dispatch(clearAll({}));

          const gender = e.target.value;
          dispatch(addGender({ gender }));

          productArray
            .filter((item) => item.category === e.target.value)
            .map((item) => {
              dispatch(addFilter({...item}));
              dispatch(addGenderArray({...item}));
            });
           
        }
      });
    }
  });


  function brandFilter(e){
   
    // dispatch(clearAll({}))
    if (e.target.checked === true) {
      console.log(filter)
      console.log(list)
      list.forEach(item => {
        for(let it of filter){    
          if(item.brand === it){
            dispatch(addFilter({...item}))
          }
        }
      })
      // dispatch(addFilter({}));
    } else {
      dispatch(removeFilter({ }));
    } 
    
  }

  let brandFilters = genderArray.map((item,index) => {
    return (
      <div key={item+index}>
        <input
          type="checkbox"
          className="filter-checkbox"
          id={item.brand}
          value={item.brand}
          onClick={(e)=>brandFilter(e)}
        ></input>
        <label for={item.brand}>{item.brand}</label>
      </div>
    );
  });

  function clear() {
    dispatch(clearAll({}));
    dispatch(fillFilter({}));
  }

  return (
    <div className="home-content">
      <div className="home-filter">
        <div className="filter-heading">FILTERS</div>
        <div
          className="filter-gender"
          id="filter-container-gender"
          style={mystyle}
        >
          {/*styling*/}
          <label for="Men" id="gender-label">
            <input
              type="Radio"
              className="filter-radiobtn"
              id="men-checkbox"
              name="radio-btn"
              value="men"
            ></input>
            Men
          </label>
          <label for="Women" id="gender-label">
            <input
              type="Radio"
              className="filter-radiobtn"
              id="women-checkbox"
              name="radio-btn"
              value="women"
            ></input>
            Women
          </label>
          <label for="Boys" id="gender-label">
            <input
              type="Radio"
              className="filter-radiobtn"
              name="radio-btn"
              value="boys"
            ></input>
            Boys
          </label>
          <label for="Girls" id="gender-label">
            <input
              type="Radio"
              className="filter-radiobtn"
              name="radio-btn"
              value="girls"
            ></input>
            Girls
          </label>
        </div>
        <div className="filter-brand" id="filter-container-brand">
          <div className="filter-brand-heading">BRAND</div>
          {brandFilters}
        </div>
      </div>
    </div>
  );
};

export default Sidepanel;
