import React, { useEffect } from 'react'
import Navbar from '../components/Navbar'
import './CSS/wishlist.css'
import Wishlistitem from '../components/Wishlistitem'
import { useSelector } from 'react-redux'

const Wishlist = () => {

    const products = useSelector(state => state.wishlist.products)
    const quantity = useSelector(state => state.wishlist.quantity)

    useEffect(()=>{
        if(quantity>0){
            document.querySelector('.wishlist-content-wrapper').getElementsByClassName.display = 'block'
            document.querySelector('.wishlist-content-empty').getElementsByClassName.display = 'none'
        }
        else{
            document.querySelector('.wishlist-content-wrapper').getElementsByClassName.display = 'none'
            document.querySelector('.wishlist-content-empty').getElementsByClassName.display = 'block'
        }
    })

  return (
    <div className='wishlist-container'>
        <Navbar/>
        <div className='wishlist-content-wrapper'>
            <div className='wishlist-content-quantity'>
                <span className='wishlist-content-quantity-1'>My Wishlist -</span>
                <span className='wishlist-content-quantity-2'> {quantity} items</span>
            </div>
            <div className='wishlist-content-list'>
                {products.map((item)=>(
                    <Wishlistitem item={item} key={item.id}/>
                ))}
            </div>
        </div>
        <div className='wishlist-content-empty'></div>
    </div>
  )
}

export default Wishlist
