import './App.css';
import Home from './Pages/Home';
import {Route, Routes} from 'react-router-dom'
import ProductDesc from './Pages/ProductDesc';
import Cart from './Pages/Cart'
import Wishlist from './Pages/Wishlist';
import { useEffect, useState } from 'react';
// import { products } from './Data';
import { useDispatch } from 'react-redux';
import { addFilterArray, addGenderArray, addproduct, clearAll, clearFilterArray, fillAll } from './Redux/productRedux';
import { publicRequest } from './requestMethods';

function App() {
  const [products,setProducts] = useState([])
  const dispatch = useDispatch()


  useEffect(()=>{
    const getProducts = async () =>{
      try{
        const res = await publicRequest.get("/product/findall")
        // setProducts(res.data)
        await res.data.forEach(e => {dispatch(addproduct(e))})
        await res.data.forEach(e => {dispatch(addFilterArray(e))})
        await res.data.forEach(e => {dispatch(addGenderArray(e))})
      }
      catch(err){}
    }

    getProducts()

    dispatch(clearFilterArray({}))
    dispatch(fillAll({}))
  },[])

  // useEffect(()=>{

  //   console.log(products,"yes")
  //   dispatch(clearFilterArray({}))
  //   dispatch(fillAll({}))
  // },[])

  return (
    <div className="App">
      <Routes>
        <Route exact path='/' element={<Home/>}></Route>
        <Route path='/product/:id' element={<ProductDesc/>}></Route>
        <Route path='/cart' element={<Cart/>}></Route>
        <Route path='/wishlist' element={<Wishlist/>}></Route>
      </Routes>
    </div>
  );
}

export default App;
