import React, { useEffect } from 'react'
import './CSS/Products.css'
import {Link} from 'react-router-dom'

const Products = ({item}) => {

  // useEffect(()=>{
  //   document.querySelector('.products-container').addEventListener('mouseover', (e)=>{
  //     e.target.querySelector('.products-btn-wrapper').style.background = 'white'
  //     e.target.querySelector('.products-btn').style.display = 'block'
  //   })

  //   document.querySelector('.products-container').addEventListener('mouseout', (e)=>{
  //     e.target.querySelector('.products-btn-wrapper').style.background = 'none'
  //     e.target.querySelector('.products-btn').style.display = 'none'
  //   })
  // })

  function wishlistOnClick(){
    console.log("hello")
  }

  const cartOnClick = (e) => {
    e.preventDefault();  
    console.log("yes")
  }

  return (
    <div className='products-container'>
      <Link to={`/product/${item._id}`} style={{'textDecoration' : 'none'}}>
          <img src={item.img} alt='product'/>
          <div className='products-info products-hover'>
           <div className='products-btn-wrapper'>
               <button className='products-btn products-wishlist-btn' onClick={wishlistOnClick}>
                 <span class="web_sprite products-wishlist-icon"></span>
                  WISHLIST
                 </button>
               <button className='products-btn products-cart-btn' onClick={cartOnClick}>
                  <span className='web_sprite products-cart-icon'></span>
                 ADD TO CART
                 </button>
           </div>
           <div className='products-desc'>
              <div className='products-desc-brand'>{item.brand}</div>
              <div className='products-desc-title'>{item.title}</div>
              <div className='products-desc-price'>&#8377;{item.price}</div>
           </div>
          </div>
      </Link>
    </div>
  )
}

export default Products
