import React, { useEffect, useState } from 'react'
import './CSS/Product.css'
import {publicRequest} from '../requestMethods'
import Products from './Products'
import { useDispatch, useSelector } from 'react-redux'
import { addproduct } from '../Redux/productRedux'
import { products } from '../Data'

const Product = () => {

  const [product,setProducts] = useState([])
  const dispatch = useDispatch();
  const filtersArray = useSelector(state => state.productFilter.filters);
  const gender = useSelector(state => state.productFilter.gender)
  const productArray = useSelector(state => state.productFilter.products)

  // useEffect(()=>{

  //   const getProducts = async () =>{
  //     try{
  //       const res = await publicRequest.get("/product/findall")
  //       setProducts(res.data)
  //     }
  //     catch(err){}
  //   }

  //   getProducts()

  // },[])

  useEffect(()=>{
    products.forEach(e => {dispatch(addproduct(e))})
  setProducts(productArray)
  },[])
  

  return (
    <div>
        <div className='product-container'>
            <div className='product-header'>
                <select className='select'>
                    <option>sort by:Recommended</option>
                    <option>Price:High-Low</option>
                    <option>Price:Low-High</option>
                </select>
            </div>
            <div className='product-info'>
              {
              productArray.filter((item) => {
                if(gender.gender === 'everyone'){
                  return item;
                }
                else if(gender != null){
                  return item.category === gender.gender;
                }
              }).filter((item) => {
                if(filtersArray.length === 0){
                  return item;
                }
                else{
                  for(let i=0;i<filtersArray.length;i++){
                    if(item.brand.includes(filtersArray[i].brand)){
                      return item;
                    }
                  }
                }
              }).map((item) => (
                  <Products item={item} key={item.id}/>
                ))
              }
            </div>
        </div>
    </div>
  )
}

export default Product
