import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { addproduct, removeproduct } from "../Redux/productRedux";
import "./CSS/Sidepanel.css";
// import { products } from '../Data'

const Sidepanel = () => {
  const mystyle = {
    //styling
    fontWeight: 600,
  };

  const dispatch = useDispatch();

  useEffect(() => {
    const checkbox = document.querySelectorAll(".filter-checkbox");

    for (let check of checkbox) {
      check.addEventListener("click", function (e) {
        const brand = e.target.value;
        if (e.target.checked === true) {
          dispatch(addproduct({ brand }));
        } else {
          dispatch(removeproduct({ brand }));
        }
      });
    }
  });

  return (
    <div className="home-content">
      <div className="home-filter">
        <div className="filter-heading">FILTERS</div>
        <div className="filter-gender" id="filter-container" style={mystyle}>
          {" "}
          {/*styling*/}
          <label for="Men" id="gender-label">
            <input
              type="Radio"
              className="filter-checkbox"
              id="men-checkbox"
            ></input>
            Men
          </label>
          <label for="Women" id="gender-label">
            <input
              type="Radio"
              className="filter-checkbox"
              id="women-checkbox"
            ></input>
            Women
          </label>
        </div>
        <div className="filter-brand" id="filter-container">
          <div className="filter-brand-heading">BRAND</div>
          <div>
            <input
              type="checkbox"
              className="filter-checkbox"
              id="WROGN"
              value="WROGN"
            ></input>
            <label for="WROGN">WROGN</label>
          </div>
          <div>
            <input
              type="checkbox"
              className="filter-checkbox"
              id="Dennis Lingo"
              value="Dennis Lingo"
            ></input>
            <label for="Dennis Lingo">Dennis Lingo</label>
          </div>
          <div>
            <input
              type="checkbox"
              className="filter-checkbox"
              id="Campus Sutra"
              value="Campus Sutra"
            ></input>
            <label for="Campus Sutra">Campus Sutra</label>
          </div>
          <div>
            <input
              type="checkbox"
              className="filter-checkbox"
              id="MANGO"
              value="MANGO"
            ></input>
            <label for="MANGO">MANGO</label>
          </div>
          <div>
            <input
              type="checkbox"
              className="filter-checkbox"
              id="Roadster"
              value="Roadster"
            ></input>
            <label for="Roadster">Roadster</label>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidepanel;
